    ESCAL056_VARS.H_PATIENT_AGE = Subtract(ESCAL056_VARS.H_PATIENT_AGE, 1)
        }
    IfCondition LessThan(ESCAL056_VARS.H_PATIENT_AGE, 18) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  H_AGE_FACTOR")
            }
    ESCAL056_VARS.H_AGE_FACTOR = 1
        }
