SubRoutine.ESDRV191_0100_ENTER_DRIVER {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  COM_CBSA_WAGE_RECORD")
        }
    COM_CBSA_WAGE_RECORD = 0
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_CALC_VERS_CD")
        }
    PPS_CALC_VERS_CD = ESDRV191_VARS.DRIVER_VERSION
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_RTC")
        }
    PPS_RTC = 0
    IfCondition Or(LessThan(B_THRU_DATE, 20050401), EqualTo(Cobol.IsNumeric(ESDRV191_VARS.B_THRU_DATE), 0)) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_RTC")
            }
    PPS_RTC = 98
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0100-EXIT-DRIVER")
            }
    RunTask(ESDRV191_0100_EXIT_DRIVER)
        }
    IfCondition EqualTo(Cobol.IsNumeric(ESDRV191_VARS.P_ESRD_RATE), 0) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_RTC")
            }
    PPS_RTC = 50
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0100-EXIT-DRIVER")
            }
    RunTask(ESDRV191_0100_EXIT_DRIVER)
        }
    IfCondition And(LessThan(B_THRU_DATE, 20110101), GreaterThan(P_ESRD_RATE, 0)) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_FINAL_PAY_AMT")
            }
    PPS_FINAL_PAY_AMT = P_ESRD_RATE
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_RTC")
            }
    PPS_RTC = 1
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0100-EXIT-DRIVER")
            }
    RunTask(ESDRV191_0100_EXIT_DRIVER)
        }
    IfCondition And(And(And(GreaterThan(B_THRU_DATE, 20101231), LessThan(B_THRU_DATE, 20140101)), EqualTo(P_PACIFIC_IS_TRUST_TERR, "2")), GreaterThan(P_ESRD_RATE, 0)) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_FINAL_PAY_AMT")
            }
    PPS_FINAL_PAY_AMT = P_ESRD_RATE
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  PPS_RTC")
            }
    PPS_RTC = 1
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0100-EXIT-DRIVER")
            }
    RunTask(ESDRV191_0100_EXIT_DRIVER)
        }
    IfCondition GreaterThan(B_THRU_DATE, 20131231) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  W_NEW_RATE1_RECORD")
            }
    W_NEW_RATE1_RECORD = 0
        }
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0800-FIND-BUNDLED-CBSA-WI")
        }
    RunTask(ESDRV191_0800_FIND_BUNDLED_CBSA_WI)
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  W_NEW_RATE1_RECORD")
        }
    W_NEW_RATE1_RECORD = 0
    IfCondition GreaterThan(B_THRU_DATE, 20081231) ThenBlock: {
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] MOVE  W_NEW_RATE1_RECORD")
            }
    W_NEW_RATE1_RECORD = 0
        }
    Debug("cobol.trace", level=3) {
    PrintMessage("[COBOL] PERFORM 0700-FIND-COMPOSITE-CBSA-WI")
        }
